import React, {Component} from 'react';
import Nav from './Nav';
import UserSignUp from "./UserSignUp"
class MyAccount extends Component{
    state = {
        isBoxVisible:false,
        username: "",
        password: ""
    }
    BetaVersionPopUp=(e)=>{
        e.preventDefault();
        this.setState({ isBoxVisible: true });
    }
    closeWindow=()=>{
        this.setState({ isBoxVisible: false });
    } 
    signIn = (e) =>  { 
        e.preventDefault();
        fetch('http://localhost:8000/api/users/checkuser/'+this.state.username, {
          method:'post',
          headers:{'Content-Type' : 'application/json'},
          body:JSON.stringify({
            password: this.state.password 
          })
        })
        .then(response=> response.json())
        .then(response=>{
          alert("You're now logged in!");
          //window.location.href = "http://localhost:3000/my-account";
        })
        .catch(err=> alert(err))
    }   
    render(){      
        let signUp = new UserSignUp(); 
        return(
          <div className="account-page">
            <Nav pageType={'interior'}/>
              <div className={`box beta-version-box ${this.state.isBoxVisible ? "" : "hidden"}`}>
              <p>Since this is still the Beta version of the app, you cannot update account information. Please check back soon to be able to sign-up and update your account whenever!</p>
              <button className="button" onClick={this.closeWindow}>Close</button>
              </div>             
                <form className="update-account-form" onSubmit={e=>this.signIn(e)}>
                    <h2>My Account</h2>                    
                    <p>Please enter your username and password to SignIn.</p>

                    <div className="form-field-group">
                        <label htmlFor="username">UserName</label>
                        <input placeholder="user123" type="username" name='username' id='username' onChange={(e)=>signUp.setForm(e)}/>
                    </div>
                    <div className="form-field-group">
                        <label htmlFor="password">Password</label>
                        <input placeholder="*******" type="password" name='password' id='password' onChange={(e)=>signUp.setForm(e)}/>
                    </div>
                    <button type="submit" onClick={this.BetaVersionPopUp}>SignIn</button>                    
                   </form> 

            </div>
        )
    }
}
export default MyAccount;
